import React, { useEffect, useRef, useState } from "react";
import "./DrawRectangle.css";
import { HiArrowUturnRight, HiArrowUturnLeft } from "react-icons/hi2";
import { AiOutlineDownload,AiOutlineZoomIn,AiOutlineZoomOut } from "react-icons/ai";
import { CiImageOn } from "react-icons/ci";
import Loading from "../Loading/Loading";

const canvasSize = 800; // Set your canvas size here
const createShadow = (size) => `${size}px ${size}px 10px #ccc inset`;
const shadowSize = 8;
const bg = {
  direction: 45,
  color: {
    dark: '#ccc',
    light: 'transparent',
  },
  size: 20,
  span: 100 / 4,
};

const DrawRectangle = () => {
  const [brushSize, setBrushSize] = useState(50);
  const [brushColor, setBrushColor] = useState("rgba(255, 199, 122)");
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [file, setFile] = useState();
    const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [imageApi, setImageApi] = useState(null);
  const [originalImage, setOriginalImage] = useState(null);
  const fileInputRef = useRef(null);
  const [isLoading, setLoading] = useState(false);
  const [canDownload, setCanDownload] = useState(false);
  const SCROLL_SENSITIVITY = 0.0005;
  const MAX_ZOOM = 10;
  const MIN_ZOOM = 0.3;
  const [zoom, setZoom] = useState(0.4);
  const [draggind, setDragging] = useState(false);
  const containerRef = useRef(null);
  const clamp = (num, min, max) => Math.min(Math.max(num, min), max);


  // เรียก setupCanvas ครั้งเดียวเมื่อ Canvas ถูกสร้างขึ้นเท่านั้น
  useEffect(() => {
    setupCanvas();
  }, []);

  useEffect(() => {
    drawImageOnCanvas();
  }, [file]);

  const setupCanvas = () => {
    const canvas = canvasRef.current;
    canvas.width = 500;
    canvas.height = 500;
    const context = canvas.getContext("2d");
    context.lineCap = "round";
    context.lineJoin = "round";
    context.lineWidth = 50;
    context.strokeStyle = brushColor;
    contextRef.current = context;
    
  };

  const startDrawing = ({ nativeEvent }) => {
    if (historyIndex !== history.length - 1) {
      // Clear the future history if we're branching off from a point in history.
      setHistory(history.slice(0, historyIndex + 1));
    }

    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
    setIsDrawing(true);
    nativeEvent.preventDefault();
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) {
      return;
    }

    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
    nativeEvent.preventDefault();
  };

  const stopDrawing = () => {
    if (isDrawing) {
      setHistory([...history, canvasRef.current.toDataURL()]);
      setHistoryIndex(history.length);
    }
    setIsDrawing(false);
  };

  const handleBrushSizeChange = (event) => {
    const newSize = parseInt(event.target.value);
    setBrushSize(newSize);
  
    // ตั้งค่า context.lineWidth ตามขนาดแปรงใหม่
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.lineWidth = newSize;
  };

  const saveImageToLocal = () => {
    const image = canvasRef.current.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = image;
    link.download = "canvas.png";
    link.click();
  };

  const handleFile = (event) => {
    setFile(event.target.files[0]);
  };

  const setImageAndDraw = (img) => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    const maxWidth = window.innerWidth - 20;
    const maxHeight = window.innerHeight - 240;
    const width = img.width;
    const height = img.height;

    if (width > maxWidth || height > maxHeight) {
      const ratio = Math.min(maxWidth / width, maxHeight / height);
      img.width = width * ratio;
      img.height = height * ratio;
    }

    canvas.width = img.width;
    canvas.height = img.height;
    context.drawImage(img, 0, 0, img.width, img.height);
    setHistory([canvas.toDataURL()]); // เพิ่มรูปภาพเริ่มต้นลงในประวัติ
    setHistoryIndex(0); // ตั้งค่า historyIndex เป็น 0
    context.lineCap = "round";
    context.lineJoin = "round";
    context.lineWidth = 50;
    context.strokeStyle = brushColor;
    setFile(null);
  };

  const drawImageOnCanvas = () => {

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        const img = new Image();
        setOriginalImage(img);

        img.onload = () => {
          setImageAndDraw(img);
        };

        img.src = e.target.result;
      };

      reader.readAsDataURL(file);
    }
  };

  const undo = () => handleHistoryChange(historyIndex - 1);
  const redo = () => handleHistoryChange(historyIndex + 1);

  const handleHistoryChange = (newIndex) => {
    if (newIndex >= 0 && newIndex < history.length) {
      setHistoryIndex(newIndex);
      const img = new Image();
      img.src = history[newIndex];
      img.onload = () => {
        const canvas = canvasRef.current;
        const context = canvas.getContext("2d");
        context.clearRect(0, 0, canvas.width, canvas.height);
        context.drawImage(img, 0, 0);
      };
    }
  };

  


  const handleWheel = (event) => {
    event.preventDefault();
    const { deltaY } = event;
    if (!draggind) {
      setZoom((zoom) =>
          clamp(zoom + deltaY * SCROLL_SENSITIVITY * -1, MIN_ZOOM, MAX_ZOOM)
      );
    }
  };

  useEffect(() => {
      const canvas = canvasRef.current;
      canvas.addEventListener('wheel', handleWheel);
      return () => {
          canvas.removeEventListener('wheel', handleWheel);
      }
  }, [handleWheel]);


  return (
    <div className="head">
      <div className="icon-image" onClick={() => fileInputRef.current.click()}>
        <CiImageOn />
      </div>
      <input onChange={handleFile} multiple={false} ref={fileInputRef} type="file" hidden />

      <div style={{ padding: 5 }} ref={containerRef}>
        {isLoading ? <Loading /> : null}
        <canvas
          ref={canvasRef}
          onMouseDown={startDrawing}
          onMouseMove={draw}
          onMouseUp={stopDrawing}
          onMouseLeave={stopDrawing}
          style={{
            transform: `scale(${zoom})`, 
          }}
              
        />
      </div>

      <div className="menuBar">
        <div className="brushClass">
          {/* <p className="brushText">Brush</p> */}
          <input 
            type="range" 
            min="10" max="150" 
            value={brushSize} 
            className="range" 
            onChange={handleBrushSizeChange} 
          />
        </div>
        <div className="buttonEraser" onClick={sendPicWish}>
          Erase
        </div>
        <div className="button icon" onClick={undo}>
          <HiArrowUturnLeft />
        </div>
        <div className="button icon" onClick={redo}>
          <HiArrowUturnRight />
        </div>
        <div
          className={`button icon ${canDownload ? "" : "disabled"}`}
          id="download_image_link"
          onClick={canDownload ? saveImageToLocal : null}
          download="canvas.png"
        >
          <AiOutlineDownload />
        </div>
      </div>
    </div>
  );
};

export default DrawRectangle;
