import React, { useEffect, useRef, useState } from "react";
import "./DrawRectangle.css";
import Loading from "../Loading/Loading";

const DrawRectangle = () => {
  const [brushSize, setBrushSize] = useState(30);
  const [brushColor, setBrushColor] = useState("rgba(255, 199, 122)");
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [file, setFile] = useState();
  const [imageDimensions, setImageDimensions] = useState({ width: 0, height: 0 });
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [imageApi, setImageApi] = useState(null);
  const [originalImage, setOriginalImage] = useState(null);
  const [isLoading, setLoading] = useState(false);

  useEffect(() => {
    setupCanvas();
  }, [brushSize, brushColor]);

  useEffect(() => {
    drawImageOnCanvas();
  }, [file]);

  const setupCanvas = () => {
    const canvas = canvasRef.current;
    canvas.width = 500;
    canvas.height = 500;
    const context = canvas.getContext("2d");
    context.lineCap = "round";
    context.lineJoin = "round";
    context.strokeStyle = "black";
    context.lineWidth = brushSize;
    context.strokeStyle = brushColor;
    contextRef.current = context;
  };

  const startDrawing = ({ nativeEvent }) => {
    if (historyIndex !== history.length - 1) {
      setHistory(history.slice(0, historyIndex + 1));
    }

    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
    setIsDrawing(true);
    nativeEvent.preventDefault();
  };

  const draw = ({ nativeEvent }) => {
    if (!isDrawing) {
      return;
    }

    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
    nativeEvent.preventDefault();
  };

  const stopDrawing = () => {
    if (isDrawing) {
      setHistory([...history, canvasRef.current.toDataURL()]);
      setHistoryIndex(history.length);
    }
    setIsDrawing(false);
  };

  const setToDraw = () => {
    contextRef.current.globalCompositeOperation = "source-over";
  };

  const handleBrushSizeChange = (event) => {
    const newSize = parseInt(event.target.value);
    setBrushSize(newSize);
  };

  const setToErase = () => {
    contextRef.current.globalCompositeOperation = "destination-out";
  };

  const saveImageToLocal = (event) => {
    const link = event.currentTarget;
    link.setAttribute("download", "canvas.png");
    const image = canvasRef.current.toDataURL("image/png");
    link.setAttribute("href", image);
  };

  const handleFile = (event) => {
    setFile(event.target.files[0]);
  };

  const setImageAndDraw = (img) => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    const maxWidth = window.innerWidth - 20;
    const maxHeight = window.innerHeight - 150;
  
    // ใช้ขนาดเริ่มต้นของรูปภาพ
    const width = imageDimensions.width;
    const height = imageDimensions.height;
  
    if (width > maxWidth || height > maxHeight) {
      const ratio = Math.min(maxWidth / width, maxHeight / height);
      img.width = width * ratio;
      img.height = height * ratio;
    }
  
    canvas.width = img.width;
    canvas.height = img.height;
    context.drawImage(img, 0, 0, img.width, img.height);
    context.lineCap = "round";
    context.lineJoin = "round";
    context.lineWidth = brushSize;
    context.strokeStyle = brushColor;
    setImageDimensions({ width: img.width, height: img.height });
    setFile(null);
  };
  

  const drawImageOnCanvas = () => {
    const canvas = canvasRef.current;

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        const img = new Image();
        setOriginalImage(img);

        img.onload = () => {
          setImageAndDraw(img);
        };

        img.src = e.target.result;
      };

      reader.readAsDataURL(file);
    }
  };

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.clearRect(0, 0, canvas.width, canvas.height);
    setHistory([]);
    setHistoryIndex(-1);
  };

  const undo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      const img = new Image();
      img.src = history[historyIndex - 1];
      img.onload = () => {
        contextRef.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        contextRef.current.drawImage(img, 0, 0);
      };
    }
  };

  const redo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      const img = new Image();
      img.src = history[historyIndex + 1];
      img.onload = () => {
        contextRef.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        contextRef.current.drawImage(img, 0, 0);
      };
    }
  };

  const sendPicWish = async () => {
    try {
      console.log("Processing...");
      setLoading(true);
      const apiUrl = "https://techhk.aoscdn.com/api/tasks/visual/inpaint";
      const apiKey = "wxxp4rxzqenh7lcvp";

      if (!originalImage && !imageApi) {
        alert("Please upload an image before processing.");
        return;
      }

      const sourceImage = imageApi ? imageApi : originalImage;

      const maxWidth = window.innerWidth - 20;
      const maxHeight = window.innerHeight - 150;
      let { width, height } = sourceImage;

      if (width > maxWidth || height > maxHeight) {
        const ratio = Math.min(maxWidth / width, maxHeight / height);
        width *= ratio;
        height *= ratio;
      }

      const canvas = document.createElement("canvas");
      canvas.width = width;
      canvas.height = height;
      const context = canvas.getContext("2d");
      context.drawImage(sourceImage, 0, 0);

      const blob = await new Promise((resolve) => {
        canvas.toBlob(resolve, "image/jpeg");
      });
      const imageFile = new File([blob], "image.jpg");
      console.log("file", imageFile);

      const changCanvas = canvasRef.current;
      const changContext = changCanvas.getContext("2d");
      const imageData = changContext.getImageData(0, 0, changCanvas.width, changCanvas.height);
      const data = imageData.data;

      for (let i = 0; i < data.length; i += 4) {
        const red = data[i];
        const green = data[i + 1];
        const blue = data[i + 2];

        if (red === 255 && green === 199 && blue === 122) {
          data[i] = 255;
          data[i + 1] = 255;
          data[i + 2] = 255;
          data[i + 3] = 255;
        } else {
          data[i] = 0;
          data[i + 1] = 0;
          data[i + 2] = 0;
          data[i + 3] = 255;
        }
      }

      const maskCanvas = document.createElement("canvas");
      const maskContext = maskCanvas.getContext("2d");
      maskCanvas.width = changCanvas.width;
      maskCanvas.height = changCanvas.height;
      maskContext.putImageData(imageData, 0, 0);

      const maskBlob = await new Promise((resolve) => {
        maskCanvas.toBlob(resolve, "image/jpeg");
      });
      const maskFile = new File([maskBlob], "mask.jpg");
      console.log("mask", maskFile);

      const formData = new FormData();
      formData.append("sync", "1");
      formData.append("image_file", imageFile);
      formData.append("mask_file", maskFile);

      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "X-API-KEY": apiKey,
        },
        body: formData,
      });

      if (response.ok) {
        setLoading(false);
        const data = await response.json();
        console.log(data);
        const imageUrl = data.data.image;

        if (imageUrl) {
          const img = new Image();
          img.crossOrigin = "Anonymous";
          img.onload = () => {
            setImageAndDraw(img);
            setImageApi(img);
          };
          img.src = imageUrl;
        } else {
          console.error("No image URL found in the API response.");
        }
      } else {
        throw new Error("An error occurred while sending the image.");
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="head">
      <div style={{ padding: 20 }}>
        {isLoading ? <Loading /> : null}
        <canvas
          className="canvas-container"
          ref={canvasRef}
          onMouseDown={startDrawing}
          onMouseMove={draw}
          onMouseUp={stopDrawing}
          onMouseLeave={stopDrawing}
          style={{
            maxWidth: "100%",
            maxHeight: "100%",
          }}
        />
      </div>

      <div className="menuBar">
        <button className="button" onClick={setToErase}>
          <i className="fa-solid fa-eraser icon-upload"></i>
          Eraser
        </button>
        <button className="buttonEraser" onClick={sendPicWish}>
          PicWish
        </button>
        <button className="button" onClick={undo}>
          Undo
        </button>
        <button className="button" onClick={redo}>
          Redo
        </button>
        <button className="button" onClick={clearCanvas}>
          Clear
        </button>
        <a
          className="button"
          id="download_image_link"
          href="download_link"
          onClick={saveImageToLocal}
        >
          Download Image
        </a>
        <input type="file" name="file" id="fileInput" onChange={handleFile} />
      </div>
    </div>
  );
};

export default DrawRectangle;
